pragma solidity ^0.8.3;
pragma abicoder v2;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@uniswap/v3-periphery/contracts/interfaces/ISwapRouter.sol";
import "./datatypes/LockedAddress.sol";

contract PozToken is ERC20, Ownable {
    event TransferFeeRateUpdated(uint256 transferFeeRate);
    event PoolFeeUpdated(uint256 poolFee);
    event TreasuryUpdated(address indexed treasury);
    event PoolAddressUpdated(address indexed poolAddress);
    event BackingReserveAddressUpdated(address indexed backingReserveAddress);
    event StableTokenAddressUpdated(address indexed stableTokenAddress);
    event LockedAddressUpdated(address indexed lockedAddress, bool status);

    using SafeERC20Upgradeable for IERC20Upgradeable;
    using SafeMath for uint256;

    uint256 constant DENOMINATOR = 10000; // 100%
    // fee when trade the POZ token
    uint256 public transferFeeRate;
    // set the pool fee to 0.3%.
    uint24 public poolFee = 3000;
    // treasury wallet to swap POZ and stable token
    address public treasury;
    // pool address of Uniswap v3 for POZ and stable token
    address public poolAddress;
    // backing reserve address to send the stable token for fee when trade the POZ token
    address public backingReserveAddress;
    // stable token address of POZ pool
    address public stableTokenAddress;
    // locked address to calculate the circulating supply of POZ token
    LockedAddress[] public lockedAddress;

    ISwapRouter public swapRouter;

    constructor(address _treasury, uint256 _transferFeeRate)
        ERC20("PozToken", "POZ")
    {
        require(_treasury != address(0), "POZTOKEN: treasury cannot be zero");
        require(
            _transferFeeRate <= DENOMINATOR,
            "POZTOKEN: transfer fee rate can not be greater than 100%"
        );
        treasury = _treasury;
        transferFeeRate = _transferFeeRate;
        emit TransferFeeRateUpdated(transferFeeRate);

        _mint(treasury, 1000000000e18);
    }

    function setTransferFeeRate(uint256 _transferFeeRate) external onlyOwner {
        require(
            _transferFeeRate <= DENOMINATOR,
            "POZTOKEN: transfer fee rate can not be greater than 100%"
        );
        transferFeeRate = _transferFeeRate;

        emit TransferFeeRateUpdated(transferFeeRate);
    }

    function setPoolFee(uint24 _poolFee) external onlyOwner {
        poolFee = _poolFee;

        emit PoolFeeUpdated(poolFee);
    }

    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "POZTOKEN: treasury cannot be zero");
        treasury = _treasury;

        emit TreasuryUpdated(treasury);
    }

    function setPoolAddress(address _poolAddress) external onlyOwner {
        require(
            _poolAddress != address(0),
            "POZTOKEN: pool address cannot be zero"
        );
        poolAddress = _poolAddress;

        swapRouter = ISwapRouter(poolAddress);

        emit PoolAddressUpdated(poolAddress);
    }

    function setBackingReserveAddress(address _backingReserveAddress)
        external
        onlyOwner
    {
        require(
            _backingReserveAddress != address(0),
            "POZTOKEN: backing reserve address cannot be zero"
        );
        backingReserveAddress = _backingReserveAddress;

        emit BackingReserveAddressUpdated(backingReserveAddress);
    }

    function setStableTokenAddress(address _stableTokenAddress)
        external
        onlyOwner
    {
        require(
            _stableTokenAddress != address(0),
            "POZTOKEN: stable token address cannot be zero"
        );
        stableTokenAddress = _stableTokenAddress;

        emit StableTokenAddressUpdated(stableTokenAddress);
    }

    function setLockedAddress(address _lockedAddress, bool _status)
        external
        onlyOwner
    {
        require(
            _lockedAddress != address(0),
            "POZTOKEN: locked address cannot be zero"
        );
        bool flag = false;
        for (uint256 i = 0; i < lockedAddress.length; i++) {
            // if address already exist, set the status of locked address
            if (lockedAddress[i].lockedAddress == _lockedAddress) {
                lockedAddress[i].status = _status;
                flag = true;
            }
        }
        // if address doesn't exist, add the locked address
        if (!flag) {
            lockedAddress.push(
                LockedAddress({lockedAddress: _lockedAddress, status: _status})
            );
        }

        emit LockedAddressUpdated(_lockedAddress, _status);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        // if the sender or recipient is token address, tx doesn't set the tranfer fee
        if (sender == address(this) || recipient == address(this)) {
            super._transfer(sender, recipient, amount);
        } else {
            // if the sender or recipient is pool address, tx will set the transfer fee
            uint256 transferFee = sender == poolAddress ||
                recipient == poolAddress
                ? (amount * transferFeeRate) / DENOMINATOR
                : 0;

            if (transferFee > 0 && poolAddress != address(0)) {
                // fee
                //// transfer POZ token to recipient
                super._transfer(sender, recipient, amount - transferFee);

                //// swap POZ token to stable token
                uint256 stableAmount = _swapExactInputSingle(transferFee);

                //// transfer stable token to backing reserve address
                IERC20Upgradeable(stableTokenAddress).transfer(
                    backingReserveAddress,
                    stableAmount
                );

                // burn
                //// get the circulating supply of POZ token
                uint256 circulatingSupply = _getCirculatingSupply();

                //// get the stable token amount
                uint256 stableTokenTotalAmount = IERC20Upgradeable(
                    stableTokenAddress
                ).balanceOf(poolAddress).add(
                        IERC20Upgradeable(stableTokenAddress).balanceOf(
                            backingReserveAddress
                        )
                    );

                uint256 expectedPozTokenAmountInPool = circulatingSupply
                    .mul(
                        IERC20Upgradeable(stableTokenAddress).balanceOf(
                            poolAddress
                        )
                    )
                    .div(stableTokenTotalAmount);

                if (expectedPozTokenAmountInPool >= balanceOf(poolAddress)) {
                    uint256 transferTokenAmount = expectedPozTokenAmountInPool
                        .sub(balanceOf(poolAddress));
                    //// transfer POZ token from treasury to pool address
                    super._transfer(treasury, poolAddress, transferTokenAmount);
                } else {
                    uint256 transferTokenAmount = balanceOf(poolAddress).sub(
                        expectedPozTokenAmountInPool
                    );
                    //// transfer POZ token from poolAddress to treasury address
                    super._transfer(poolAddress, treasury, transferTokenAmount);
                }
            } else {
                // transfer POZ token to recipient
                super._transfer(sender, recipient, amount);
            }
        }
    }

    /// @notice swapExactInputSingle swaps a fixed amount of POZ for a maximum possible amount of Stable token
    /// using the POZ/STABLE 0.3% pool by calling `exactInputSingle` in the swap router.
    /// @dev The calling address must approve this contract to spend at least `amountIn` worth of its POZ for this function to succeed.
    /// @param amountIn The exact amount of POZ that will be swapped for Stable token.
    /// @return amountOut The amount of stable token received.
    function _swapExactInputSingle(uint256 amountIn)
        internal
        returns (uint256 amountOut)
    {
        // Approve the router to spend POZ.
        _approve(address(this), poolAddress, amountIn);

        // Naively set amountOutMinimum to 0. In production, use an oracle or other data source to choose a safer value for amountOutMinimum.
        // We also set the sqrtPriceLimitx96 to be 0 to ensure we swap our exact input amount.
        ISwapRouter.ExactInputSingleParams memory params = ISwapRouter
            .ExactInputSingleParams({
                tokenIn: address(this),
                tokenOut: stableTokenAddress,
                fee: poolFee,
                recipient: address(this),
                deadline: block.timestamp,
                amountIn: amountIn,
                amountOutMinimum: 0,
                sqrtPriceLimitX96: 0
            });

        // The call to `exactInputSingle` executes the swap.
        amountOut = swapRouter.exactInputSingle(params);
    }

    // get the circulating supply of POZ token
    function _getCirculatingSupply() internal view returns (uint256) {
        uint256 circulatingSupply = totalSupply();
        for (uint256 i = 0; i < lockedAddress.length; i++) {
            if (lockedAddress[i].status) {
                uint256 tokenAmount = balanceOf(lockedAddress[i].lockedAddress);
                circulatingSupply = circulatingSupply.sub(tokenAmount);
            }
        }

        return circulatingSupply;
    }
}
